using System;

namespace Day_2
{
    class Program
    {
        private static readonly string[] GAME_INPUT =
        {
            "",
            "",
            "",
            "",
            ""
        };

        static void Main(string[] args)
        {
            int currentNumber = 5;
            int foundNumber = 0;
            int codeIndex = 0;
            int currentRow = 0;
            int currentColumn = 0;
            int[] code = { 0, 0, 0, 0, 0 };

            foreach(string inputToFind in GAME_INPUT)
            {
                foreach(char charInInput in inputToFind)
                {
                    foundNumber = currentNumber;

                    // Expensive - Meh, this is not the best way to do it ;)
                    currentRow = (int)Math.Ceiling((double)foundNumber / 3);
                    currentColumn = (foundNumber % 3);
                    if (currentColumn == 0)
                    {
                        currentColumn = 3; // Just so its consistent with row.
                    }

                    switch(charInInput)
                    {
                        case 'U':
                            if (currentRow != 1)
                            {
                                foundNumber -= 3;
                            }
                            break;
                        case 'D':
                            if (currentRow != 3)
                            {
                                foundNumber += 3;
                            }
                            break;
                        case 'L':
                            if (currentColumn != 1)
                            {
                                foundNumber -= 1;
                            }
                            break;
                        case 'R':
                            if(currentColumn != 3)
                            {
                                foundNumber += 1;
                            }
                            break;
                    }

                    currentNumber = foundNumber;
                }

                code[codeIndex] = foundNumber;
                codeIndex++;
            }

            Console.WriteLine("Code is: " + PrintCode(code));
            Console.ReadKey();
        }

        private static string PrintCode(int[] code)
        {
            string output = "";

            foreach(int number in code)
            {
                output += number;
            }

            return output;
        }
    }
}
